import React, { useState } from 'react';
import axios from 'axios';
import './DataInputForm.css';

const DataInputForm = () => {
    const [formData, setFormData] = useState({
        type: 'transportation',
        emissions: '',
        details: {}
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        setSuccess('');

        try {
            const token = localStorage.getItem('token');
            await axios.post('http://localhost:5000/api/data/add', formData, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setSuccess('Data added successfully!');
            setFormData({
                type: 'transportation',
                emissions: '',
                details: {}
            });
        } catch (err) {
            setError(err.response?.data?.message || 'Error adding data');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="data-input-form">
            <h2>Add Carbon Footprint Data</h2>
            
            {error && <div className="error-message">{error}</div>}
            {success && <div className="success-message">{success}</div>}
            
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="type">Activity Type</label>
                    <select
                        id="type"
                        value={formData.type}
                        onChange={(e) => setFormData({...formData, type: e.target.value})}
                    >
                        <option value="transportation">Transportation</option>
                        <option value="electricity">Electricity</option>
                        <option value="food">Food</option>
                        <option value="other">Other</option>
                    </select>
                </div>

                <div className="form-group">
                    <label htmlFor="emissions">Emissions (kg CO2)</label>
                    <input
                        type="number"
                        id="emissions"
                        value={formData.emissions}
                        onChange={(e) => setFormData({...formData, emissions: e.target.value})}
                        required
                        min="0"
                        step="0.01"
                    />
                </div>

                <button type="submit" disabled={loading}>
                    {loading ? 'Adding...' : 'Add Data'}
                </button>
            </form>
        </div>
    );
};

export default DataInputForm;
