import React, { useState } from 'react';
import axios from 'axios';

const Notifications = () => {
    const [threshold, setThreshold] = useState(0);
    const [message, setMessage] = useState('');

    const handleCheckThreshold = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const res = await axios.post(
                'http://localhost:5000/api/notifications/check',
                { threshold },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setMessage(res.data.message);
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <div>
            <h1>Set Threshold</h1>
            <form onSubmit={handleCheckThreshold}>
                <input type="number" placeholder="Threshold" value={threshold} onChange={(e) => setThreshold(e.target.value)} />
                <button type="submit">Check</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default Notifications;
