import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
    const [emissionData, setEmissionData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('token');
                const res = await axios.get('http://localhost:5000/api/data', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setEmissionData(res.data);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch data');
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    const chartData = {
        labels: ['Energy', 'Transportation', 'Waste'],
        datasets: [
            {
                label: 'Carbon Emissions (kg CO2)',
                data: emissionData.length > 0 ? [
                    emissionData.reduce((sum, data) => sum + data.energy, 0),
                    emissionData.reduce((sum, data) => sum + data.transportation, 0),
                    emissionData.reduce((sum, data) => sum + data.waste, 0)
                ] : [0, 0, 0],
                backgroundColor: ['#4CAF50', '#2196F3', '#FF5722'],
                borderColor: ['#388E3C', '#1976D2', '#E64A19'],
                borderWidth: 1,
            },
        ],
    };

    const options = {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
            legend: {
                position: 'top',
            },
            title: {
                display: true,
                text: 'Total Carbon Emissions by Category',
                font: {
                    size: 20
                }
            },
        },
        scales: {
            y: {
                beginAtZero: true,
                title: {
                    display: true,
                    text: 'CO2 Emissions (kg)'
                }
            }
        }
    };

    if (loading) return (
        <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading dashboard data...</p>
        </div>
    );

    if (error) return (
        <div className="error-container">
            <div className="alert alert-error">{error}</div>
        </div>
    );

    return (
        <div className="dashboard-container">
            <div className="dashboard-header">
                <h1>Carbon Footprint Dashboard</h1>
                <p className="dashboard-subtitle">Track and monitor your organization's environmental impact</p>
            </div>

            <div className="stats-grid">
                <div className="stat-card">
                    <i className="fas fa-bolt stat-icon"></i>
                    <h3>Total Energy Emissions</h3>
                    <p className="stat-value">{emissionData.reduce((sum, data) => sum + data.energy, 0)} kg CO2</p>
                </div>
                <div className="stat-card">
                    <i className="fas fa-car stat-icon"></i>
                    <h3>Total Transportation Emissions</h3>
                    <p className="stat-value">{emissionData.reduce((sum, data) => sum + data.transportation, 0)} kg CO2</p>
                </div>
                <div className="stat-card">
                    <i className="fas fa-trash stat-icon"></i>
                    <h3>Total Waste Emissions</h3>
                    <p className="stat-value">{emissionData.reduce((sum, data) => sum + data.waste, 0)} kg CO2</p>
                </div>
            </div>

            <div className="chart-container">
                <Bar data={chartData} options={options} height={300} />
            </div>
        </div>
    );
};

export default Dashboard;
