import React, { useState } from "react";

const Profile = () => {
  const [email] = useState("dhyyrya@gmail.com");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleUpdate = () => {
    if (password && password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }
    alert("Profile updated successfully!");
  };

  const handleLogout = () => {
    alert("Logged out!");
  };

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800">
      {/* Navbar */}
      <header className="bg-white shadow-md px-6 py-4 flex justify-between items-center">
        <h1 className="text-xl font-bold text-green-700">EcoTrack</h1>
        <nav className="space-x-6 font-medium">
          <a href="/dashboard" className="hover:text-green-600">Dashboard</a>
          <a href="/input-data" className="hover:text-green-600">Input Data</a>
          <a href="/reports" className="hover:text-green-600">Reports</a>
          <a href="/profile" className="hover:text-green-600">Profile</a>
        </nav>
        <button 
          onClick={handleLogout} 
          className="bg-green-700 text-white px-4 py-1 rounded hover:bg-green-800"
        >
          Logout
        </button>
      </header>

      {/* Profile Form */}
      <main className="flex justify-center items-center mt-12">
        <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
          <h2 className="text-2xl font-semibold text-center mb-6">Update Profile</h2>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-1">Email</label>
              <input
                type="email"
                value={email}
                readOnly
                className="w-full p-2 border rounded bg-gray-100"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">New Password (optional)</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full p-2 border rounded"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Confirm New Password</label>
              <input
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                className="w-full p-2 border rounded"
              />
            </div>

            <button
              onClick={handleUpdate}
              className="w-full bg-green-700 text-white py-2 rounded hover:bg-green-800"
            >
              Update Profile
            </button>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Profile;
