import React, { useState } from 'react';
import axios from 'axios';

const DataInputForm = () => {
    const [energy, setEnergy] = useState(0);
    const [transportation, setTransportation] = useState(0);
    const [waste, setWaste] = useState(0);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            await axios.post(
                'http://localhost:5000/api/data/add',
                { energy, transportation, waste },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert('Data added successfully!');
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="number" placeholder="Energy" value={energy} onChange={(e) => setEnergy(e.target.value)} />
            <input type="number" placeholder="Transportation" value={transportation} onChange={(e) => setTransportation(e.target.value)} />
            <input type="number" placeholder="Waste" value={waste} onChange={(e) => setWaste(e.target.value)} />
            <button type="submit">Submit</button>
        </form>
    );
};

export default DataInputForm;
