import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Profile = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        confirmPassword: ''
    });
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState({ type: '', text: '' });

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/api/profile', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFormData(prev => ({
                    ...prev,
                    email: response.data.email
                }));
            } catch (err) {
                console.error('Error fetching user data:', err);
            }
        };
        fetchUserData();
    }, []);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleUpdateProfile = async (e) => {
        e.preventDefault();
        if (formData.password !== formData.confirmPassword) {
            setMessage({ type: 'error', text: 'Passwords do not match' });
            return;
        }

        setLoading(true);
        setMessage({ type: '', text: '' });

        try {
            const token = localStorage.getItem('token');
            const updateData = { email: formData.email };
            if (formData.password) updateData.password = formData.password;

            await axios.put('http://localhost:5000/api/profile/update', updateData, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setMessage({ type: 'success', text: 'Profile updated successfully!' });
            setFormData(prev => ({ ...prev, password: '', confirmPassword: '' }));
        } catch (err) {
            setMessage({ type: 'error', text: err.response?.data?.message || 'Error updating profile' });
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gray-100">
            {/* NavBar */}
            <div className="bg-white shadow-md px-6 py-4 flex justify-between items-center">
                <div className="text-2xl font-bold text-green-700 flex items-center space-x-2">
                    <span role="img" aria-label="leaf">ðŸŒ±</span>
                    <span>EcoTrack</span>
                </div>
                <div className="space-x-4">
                    <Link to="/dashboard" className="font-medium hover:text-green-700">Dashboard</Link>
                    <Link to="/add-activity" className="font-medium hover:text-green-700">Input Data</Link>
                    <Link to="/reports" className="font-medium hover:text-green-700">Reports</Link>
                    <Link to="/profile" className="font-medium hover:text-green-700">Profile</Link>
                    <button className="bg-red-500 text-white px-4 py-1 rounded hover:bg-red-600">Logout</button>
                </div>
            </div>

            {/* Form Card */}
            <div className="max-w-xl mx-auto mt-10 bg-white shadow-md rounded-lg p-6">
                <h2 className="text-2xl font-semibold mb-4 text-green-800">Update Profile</h2>

                {message.text && (
                    <div className={`mb-4 p-3 rounded ${message.type === 'error' ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700'}`}>
                        {message.text}
                    </div>
                )}

                <form onSubmit={handleUpdateProfile} className="space-y-5">
                    <div>
                        <label className="block font-medium mb-1">Email</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            className="w-full border border-gray-300 rounded px-3 py-2"
                        />
                    </div>
                    <div>
                        <label className="block font-medium mb-1">New Password (optional)</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            className="w-full border border-gray-300 rounded px-3 py-2"
                        />
                    </div>
                    <div>
                        <label className="block font-medium mb-1">Confirm New Password</label>
                        <input
                            type="password"
                            name="confirmPassword"
                            value={formData.confirmPassword}
                            onChange={handleChange}
                            className="w-full border border-gray-300 rounded px-3 py-2"
                        />
                    </div>
                    <button
                        type="submit"
                        disabled={loading}
                        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                    >
                        {loading ? 'Updating...' : 'Update Profile'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Profile;
